package crypto;import crypto.constant.AlgorithmConstant;import crypto.digest.MD5;import crypto.digest.SHA1;import crypto.digest.SHA256;import crypto.digest.SM3;import crypto.model.Algorithm;/** * @author loquy * @date 2023/02/09 9:23 */public class DigestFactory {    public static Digest getDigestFactory(Algorithm algorithm) throws Exception {        switch (algorithm.getHashAlgorithm()) {            case AlgorithmConstant.MD5:                return new MD5(algorithm);            case AlgorithmConstant.SHA1:                return new SHA1(algorithm);            case AlgorithmConstant.SHA256:                return new SHA256(algorithm);            case AlgorithmConstant.SM3:                return new SM3(algorithm);            default:                throw new Exception("没有找到此算法：" + algorithm.getHashAlgorithm());        }    }}